INSERT INTO `products` (`id`, `name`, `description`, `content`, `menu_id`, `price`, `price_sale`, `stock`, `active`, `created_at`, `updated_at`, `thumb`) VALUES
(8, 'Dây nhảy thể thao trẻ em 2,8M Tay cầm trong suốt PD1', 'Dây nhảy đua Thiết bị thể thao [MỚI]', '<p>C&aacute;c t&iacute;nh năng: 1. Th&iacute;ch hợp cho trẻ em: D&acirc;y nhảy n&agrave;y được thiết kế d&agrave;nh ri&ecirc;ng cho trẻ em, với chiều d&agrave;i 2,8m, c&oacute; thể đ&aacute;p ứng nhu cầu chiều cao của trẻ v&agrave; cho ph&eacute;p trẻ tận hưởng niềm vui khi nhảy d&acirc;y tốt hơn. 2. Thiết kế tay cầm trong suốt: Tay cầm được l&agrave;m bằng chất liệu trong suốt, thời trang v&agrave; dễ cầm nắm, kh&ocirc;ng dễ trượt, tăng độ ổn định v&agrave; thoải m&aacute;i trong qu&aacute; tr&igrave;nh nhảy d&acirc;y. 3. D&acirc;y nhảy tốc độ: D&acirc;y nhảy n&agrave;y th&iacute;ch hợp cho c&aacute;c sự kiện nhảy d&acirc;y tốc độ, với trọng lượng v&agrave; v&ograve;ng quay &ecirc;m &aacute;i, gi&uacute;p trẻ n&acirc;ng cao tốc độ v&agrave; t&iacute;nh linh hoạt. 4. Chất liệu cao cấp: Được l&agrave;m bằng chất liệu cao cấp c&oacute; độ bền v&agrave; chống m&agrave;i m&ograve;n, c&oacute; khả năng chịu được c&aacute;c chuyển động nhảy d&acirc;y trong thời gian d&agrave;i v&agrave; kh&ocirc;ng dễ bị đứt, hư hỏng. 5. Dễ d&agrave;ng cất giữ: D&acirc;y nhảy c&oacute; chiều d&agrave;i ph&ugrave; hợp, gi&uacute;p bạn dễ d&agrave;ng cất giữ v&agrave; mang theo, thuận tiện cho b&eacute; tham gia c&aacute;c hoạt động nhảy d&acirc;y ngo&agrave;i trời v&agrave; trong nh&agrave;. Th&ocirc;ng số kỹ thuật: Chất liệu: PVC Đặc điểm kỹ thuật: 15 * 11 * 2cm Tổng trọng lượng: khoảng 60g Bao b&igrave;: OPP G&oacute;i bao gồm: Nhảy d&acirc;y * 1 chiếc</p>\r\n\r\n<div class=\"ddict_btn\" style=\"left:1000px; top:11px\"><img src=\"chrome-extension://cianljdimgjlpmjllcbahmpdnicglaap/logo/48.png\" /></div>', 5, 55000, 23000, 96, 1, '2024-06-13 21:07:31', '2024-07-16 11:18:39', '/storage/uploads/2024/06/14/pd1.jpg'),
(9, 'Dây Nhảy Tập Thể Dục Giảm Cân Không Dây Chuyên Nghiệp PD2', 'Thiết bị thể thao [MỚI]', '<p>Chất liệu: PP + PVC + silicon</p>\r\n\r\n<p>K&iacute;ch thước: Chiều d&agrave;i tay cầm khoảng. 13,5cm, b&oacute;ng kh&ocirc;ng d&acirc;y xấp xỉ trực tiếp. 75cm</p>\r\n\r\n<p>Trọng lượng: 270 g / 1 cặp kh&ocirc;ng tải, 390 g / 1 cặp kh&ocirc;ng tải</p>\r\n\r\n<p><img alt=\"\" src=\"https://down-vn.img.susercontent.com/file/sg-11134201-7rbmu-llykqr1wgxuhe2\" style=\"height:800px; width:800px\" /></p>', 5, 66000, 42000, 198, 1, '2024-06-13 21:12:17', '2024-07-16 11:16:50', '/storage/uploads/2024/06/14/pd2.jfif'),
(10, 'Tạ Tay Nữ Chuyên Tập Gym Yoga Pilates Thể Dục Tại Nhà GEPO PD3', 'Thiết bị thể thao [MỚI]', '<p><strong>Chất liệu: Vỏ Nhựa PP + L&otilde;i Xi măng</strong></p>\r\n\r\n<p>Size S: 2kg</p>\r\n\r\n<p>Size M: 4kg</p>\r\n\r\n<p>Size L: 5kg</p>\r\n\r\n<p><img alt=\"\" src=\"https://down-vn.img.susercontent.com/file/vn-11134207-7r98o-lqw2c4t3k2isf2\" style=\"height:850px; width:850px\" /></p>\r\n\r\n<p>*Sản phẩm tốt nhất trong ph&acirc;n kh&uacute;c gi&aacute;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>* Sản phẩm được kiểm tra kĩ c&agrave;ng, cẩn thận v&agrave; tư vấn nhiệt t&igrave;nh trước khi g&oacute;i h&agrave;ng giao cho Qu&yacute; Kh&aacute;ch</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>* Cam kết sản phẩm giống h&igrave;nh ảnh đến 100%</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>* Kh&aacute;c biệt duy nhất l&agrave; m&agrave;u sắc do &aacute;nh s&aacute;ng v&agrave; độ ph&acirc;n giải c&aacute;c m&agrave;n h&igrave;nh hiển thị nhưng KH&Ocirc;NG Đ&Aacute;NG KỂ.</p>\r\n\r\n<p><img alt=\"\" src=\"https://down-vn.img.susercontent.com/file/vn-11134207-7r98o-lqw2c4t3h9dwaf\" style=\"height:850px; width:850px\" /></p>', 5, 120000, 99000, 16, 1, '2024-06-13 21:23:16', '2024-07-16 11:21:41', '/storage/uploads/2024/06/14/pd3.jpg'),
(11, 'Dây kháng lực số 8 tập yoga,gym,pilates PD4', 'Thiết bị thể thao [MỚI]', '<p>D&acirc;y kh&aacute;ng lực số 8 tập yoga,gym,pilates</p>\r\n\r\n<p>---</p>\r\n\r\n<p>ĐẶC T&Iacute;NH KỸ THUẬT D&Acirc;Y KH&Aacute;NG LỰC SỐ 8</p>\r\n\r\n<p>-Được l&agrave;m bằng TPE thế hệ mới th&acirc;n thiện với con người v&agrave; m&ocirc;i trường, c&oacute; độ đ&agrave;n hồi tốt, lực đ&agrave;n hồi mạnh, t&iacute;nh an to&agrave;n cao,bền bỉ.</p>\r\n\r\n<p>-Tay cầm xốp EVA &ecirc;m &aacute;i, độ nh&aacute;m vừa phải gi&uacute;p tăng ma s&aacute;t, chắc tay khi tập luyện mang đến sự tự t&iacute;n rất cần thiết</p>\r\n\r\n<p>-Với ph&acirc;n loại đ&uacute;c: Sản phẩm được l&agrave;m ho&agrave;n to&agrave;n từ cao su silicon non với độ co gi&atilde;n v&agrave; đ&agrave;n hồi tốt, bền bỉ</p>\r\n\r\n<p>-K&iacute;ch thước: 400x130x10mm v&agrave; nặng khoảng 60g</p>\r\n\r\n<p>---</p>\r\n\r\n<p>ỨNG DỤNG D&Acirc;Y KH&Aacute;NG LỰC SỐ 8 TRONG TẬP GYM,YOGA,PILATES</p>\r\n\r\n<p>-D&acirc;y K&eacute;o Đ&agrave;n Hồi l&agrave; dụng cụ tập gym tại nh&agrave; được sử dụng nhiều với c&aacute;c b&agrave;i tập cardio, luyện sức bền hay thể lực, cung cấp lực đ&agrave;n hồi tốt, n&acirc;ng cao hiệu quả luyện tập cho cơ đ&ugrave;i săn chắc, eo thon.</p>\r\n\r\n<p>-D&acirc;y k&eacute;o tập gym đa năng c&oacute; thể tập đa chiều n&ecirc;n c&oacute; thể ph&aacute;t triển c&aacute;c nh&oacute;m cơ nhỏ.</p>\r\n\r\n<p>-D&acirc;y được thiết kế dễ mang theo, c&oacute; thể tập tại nh&agrave;, khi đi du lịch hoặc đi c&ocirc;ng t&aacute;c xa, c&oacute; thể tập mọi l&uacute;c mọi nơi.</p>\r\n\r\n<p>-Khi tập với d&acirc;y đ&agrave;n hồi kh&aacute;ng lực, c&aacute;c nh&oacute;m cơ của bạn sẽ ph&aacute;t triển c&acirc;n bằng, kh&ocirc;ng so lệch.</p>', 5, 66000, 39000, 36, 1, '2024-06-13 21:25:56', '2024-07-16 11:21:00', '/storage/uploads/2024/06/14/pd4.jfif'),
(41, 'Dây Tập Bụng, Dây Kéo Đàn Hồi Kháng Lực 4 ống, Cao Su Tập Bụng Tại Nhà, bộ căng bàn đạp, tập yoga tại nhà, giãn cơ toàn thân PD17', 'Chất liệu: TPE + bọt', '<p>Th&ocirc;ng số kỹ thuật:</p>\r\n\r\n<p>T&ecirc;n: D&acirc;y đeo kh&aacute;ng b&agrave;n đạp</p>\r\n\r\n<p>Chất liệu: TPE + bọt</p>\r\n\r\n<p>K&iacute;ch thước: khoảng 26 * 48cm / 10,24 * 18,9 inch</p>\r\n\r\n<p>Chiều d&agrave;i k&eacute;o d&agrave;i: khoảng 120cm / 47,24 inch</p>\r\n\r\n<p><img alt=\"\" src=\"https://down-vn.img.susercontent.com/file/sg-11134201-7rd51-lu90426u79rw1f\" style=\"height:1024px; width:1024px\" /><img alt=\"\" src=\"https://down-vn.img.susercontent.com/file/sg-11134201-7rd41-lu90424w8ow189\" style=\"height:1024px; width:1024px\" /><img alt=\"\" src=\"https://down-vn.img.susercontent.com/file/sg-11134201-7rd3k-lu903ycxrnwjc6\" style=\"height:1024px; width:1024px\" /></p>\r\n\r\n<div class=\"ddict_btn\" style=\"left:215.035px; top:126px\"><img src=\"chrome-extension://cianljdimgjlpmjllcbahmpdnicglaap/logo/48.png\" /></div>', 5, 89000, 49000, 413, 1, '2024-07-02 16:12:59', '2024-07-16 01:55:43', '/storage/uploads/2024/07/03/pd17.jfif');

